mapper.select:
select 

from t1user
where username = #{username,jdbcType=VARCHAR}
  and paswd = #{paswd,jdbcType=VARCHAR}
  and id = #{id,jdbcType=INTEGER}

mapper.delete:
delete from t1user
where username = #{username,jdbcType=VARCHAR}
  and paswd = #{paswd,jdbcType=VARCHAR}
  and id = #{id,jdbcType=INTEGER}
